import Noidung from '../edit_tintuc/noidung';
import Tieude from '../edit_tintuc/tieude';
import React, { useState, useEffect } from 'react';
function Xuhuong() {
  const [postArray, setPostArray] = useState([]);
  useEffect(() => {
    async function LayDL() {
      try {
        const requestUrl = "http://10.144.13.35:8080/vnnews-api/getNewsList";
        var raw = JSON.stringify({ "header": { "requestTime": 0 }, "body": "eyJjYXRlSWQiOjMxLCJvcmRlclR5cGUiOjAsImdyb3VwVHlwZSI6MTMsImNvbnRlbnRUeXBlIjowLCJmaWx0ZXJUeXBlIjowLCJ0cmVuZGluZyI6MCwicGFnZSI6MSwicGFnZVNpemUiOjEwMH0=" });
        const response = await fetch(requestUrl, {
          mode: 'cors',
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            "Access-Control-Allow-Origin": "*"
          },
          body: raw
        });
        const responsejson = await response.json();
        //const {data} = responsejson;
        //setPostArray(data);
        console.log(responsejson)
      } catch (e) {
        console.log(e.message);
      }
    }
    setTimeout(LayDL(), 20000);

  }, []);
  return (
    <div className="news_xh">
      <div className="news_td">
        <div className="td_title">
          <i className="fas fa-chart-line fa-2x" style={{ color: 'blue' }} />{' '}
          <Tieude title="Xu hướng" />
        </div>{' '}
        <div className="td_tag">
          <div className="tag">
            <a href> Học sinh </a>
          </div>
          <div className="tag">
            <a href> Học sinh </a>
          </div>
          <div className="tag">
            <a href> Học sinh </a>
          </div>
        </div>
      </div>
      <div className="news_nd">
        <Noidung />

        <Noidung />
        <Noidung />
      </div>
    </div>
  );
}

export default Xuhuong;
