import Noidung from '../edit_tintuc/noidung';

import Tieude from '../edit_tintuc/tieude';
import React, { useState, useEffect } from 'react';
import Noidung1 from '../edit_tintuc/noidung1';
import Noidung2 from '../edit_tintuc/noidung2';
//import axios from 'axios';

function Xuhuong() {
  const [postArray, setPostArray] = useState([]);
  useEffect(() => {
    async function LayDL() {
      try {
        var axios = require('axios');
        var data = JSON.stringify({ "body": "{\"id\":3,\"page\":1,\"pageSize\":1000}" });

        var config = {
          mode: "no-cors",
          method: 'POST',
          url: 'http://192.168.1.120:8084/api/getCommentList',
          headers: {
            'Content-Type': 'application/json',
            'Accept': '*/*',

            'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Methods': 'GET, POST, PATCH, PUT, DELETE, OPTIONS',
            'Access-Control-Allow-Headers': 'Origin, Content-Type',
            'Connection': 'keep-alive'
          },
          data: data
        };

        await axios({
          mode: "no-cors",
          method: 'POST',
          url: 'http://192.168.1.120:8084/api/getCommentList',
          headers: {
            'Content-Type': 'application/json',
            'Accept': '*/*',

            'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Methods': 'GET, POST, PATCH, PUT, DELETE, OPTIONS',
            'Access-Control-Allow-Headers': 'Origin, Content-Type',
            'Connection': 'keep-alive'
          },
          body: data
        })
          .then(function (response) {
            console.log(JSON.stringify(response.data));
          })
          .catch(function (error) {
            console.log(error);
          });

      } catch (e) {
        console.log(e.error);
      }
    }
    LayDL();
  }, []);
  return (
    <div className="news_xh">
      <div className="news_td">
        <div className="td_title">
          <i className="fas fa-chart-line fa-2x" style={{ color: 'blue' }} />{' '}
          <Tieude title="Xu hướng" />
        </div>{' '}
        <div className="td_tag">
          <div className="tag">
            <a href> Học sinh </a>
          </div>
          <div className="tag">
            <a href> Corona </a>
          </div>
          <div className="tag">
            <a href> Ngoại hạng </a>
          </div>
        </div>
      </div>
      <div className="news_nd">
        <Noidung />
        <Noidung1 />
        <Noidung2 />
      </div>
    </div>
  );
}

export default Xuhuong;
