  slides[slideIndex - 1].style.display = 'block';
  dots[slideIndex - 1].className += ' active';
}
setInterval(function () {
  var stt = document.getElementsByClassName('mySlides');
  if (dem == null || dem > stt.length) {
    dem = 1;
  }
  currentSlide(dem);
  dem++;
}, 10000);

