import Noidung from '../edit_tintuc/noidung';
import Tieude from '../edit_tintuc/tieude';
import React, { useState, useEffect } from 'react';
function Xuhuong() {
  const [postArray, setPostArray] = useState([]);
  useEffect(() => {
    async function LayDL() {
      try {
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({ "header": { "requestTime": 0 }, "body": "eyJjYXRlSWQiOjMxLCJvcmRlclR5cGUiOjAsImdyb3VwVHlwZSI6MTMsImNvbnRlbnRUeXBlIjowLCJmaWx0ZXJUeXBlIjowLCJ0cmVuZGluZyI6MCwicGFnZSI6MSwicGFnZVNpemUiOjEwMH0=" });

        var requestOptions = {
          mode: 'no-cors',
          method: 'POST',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };

        fetch("http://10.144.14.56:8084/api//getNewsList", requestOptions)
          .then(response => response)
          .then(result => console.log(result))
          .catch(error => console.log('error', error));
      } catch (e) {
        console.log(e.message);
      }
    }
    LayDL();
  }, []);
  return (
    <div className="news_xh">
      <div className="news_td">
        <div className="td_title">
          <i className="fas fa-chart-line fa-2x" style={{ color: 'blue' }} />{' '}
          <Tieude title="Xu hướng" />
        </div>{' '}
        <div className="td_tag">
          <div className="tag">
            <a href> Học sinh </a>
          </div>
          <div className="tag">
            <a href> Học sinh </a>
          </div>
          <div className="tag">
            <a href> Học sinh </a>
          </div>
        </div>
      </div>
      <div className="news_nd">
        <Noidung />

        <Noidung />
        <Noidung />
      </div>
    </div>
  );
}

export default Xuhuong;
