import React, { useRef } from 'react';
function Ndanh() {
  var KichThuoc = 376;
  var ChuyenSlide = useRef(null);
  var Chuyen = 0;

  function Next() {
    var Img = ChuyenSlide.current.getElementsByTagName('img');
    var Max = KichThuoc * (Img.length - 1);
    if (Chuyen < Max) Chuyen += KichThuoc;
    else Chuyen = 0;
    ChuyenSlide.current.style.marginLeft = '-' + Chuyen + 'px';
  }

  function Back() {
    var Img = ChuyenSlide.current.getElementsByTagName('img');
    var Max = KichThuoc * (Img.length - 1);
    if (Chuyen === 0) Chuyen = Max;
    else Chuyen -= KichThuoc;
    ChuyenSlide.current.style.marginLeft = '-' + Chuyen + 'px';
  }
  return (
    <div className="slide_picture">
      <div className="chuyen-slide" ref={ChuyenSlide}>
        <div className="slide-tintuc">
          <a href="true">
            <img
              src="https://images.digitoday.vn/NEWS/CONTENT/139918/1610438181911_a1.jpg"
              alt="true"
            />
          </a>
        </div>
        <div className="slide-tintuc">
          <a href="true">
            <img
              src="https://images.digitoday.vn/NEWS/CONTENT/139918/1610438181916_a2.jpg"
              alt="true"
            />
          </a>
        </div>
        <div className="slide-tintuc">
          <a href="true">
            <img
              src="https://images.digitoday.vn/NEWS/CONTENT/139918/1610438181920_a3.jpg"
              alt="true"
            />
          </a>
        </div>
      </div>
      <p className="prev" onClick={Back}>
        ❮
      </p>
      <p className="next" onClick={Next}>
        ❯
      </p>
    </div>
  );
}
export default Ndanh;
